import axios from 'axios'
import {
    GET_ALL_FILMS,
    GET_ALL_STARSHIPS,
    GET_ALL_VEHICLES
} from '../Actions/types.js'

export const getAllFilms = () => {
    return function(dispatch){
        try {
            let json = axios(`${process.env.REACT_APP_URL_SWAPI}/films`)
            return dispatch({type: GET_ALL_FILMS, payload: json.data})
        } catch (error) {
            console.log(error)
        }
    }
}

export const getAllStarships = () => {
    return  function(dispatch){
        try {
            let tempjson = []
            let json = []
            let count = 0
            while(count<3){
                tempjson =  axios(`${process.env.REACT_APP_URL_SWAPI}/starships/?page=${count}`)
                json.concat(tempjson)
            }
            return dispatch({type: GET_ALL_STARSHIPS, payload: json.data})
        } catch (error) {
            console.log(error)
        }
    }
}

export const getAllVehicles = (page) => {
    return async function(dispatch){
        try {
            let json = await axios(`${process.env.REACT_APP_URL_SWAPI}/vehicles/?page=${Number(page)}`)
            return dispatch({type: GET_ALL_VEHICLES, payload: json.data})
        } catch (error) {
            console.log(error)
        }
    }
}
