import {
    GET_ALL_FILMS,
    GET_ALL_STARSHIPS,
    GET_ALL_VEHICLES
} from "../Actions/types" 

const initialState = {
    films : [],
    starships : [],
    vehicles : []
}

export default function rootReducer(state = initialState, {type, payload}){
    switch(type){
        case GET_ALL_FILMS:
            return{
                ...state,
                films : payload
            }
        
        case GET_ALL_STARSHIPS:
            return{
                ...state,
                starships : payload
            }
        
        case GET_ALL_VEHICLES:
            return{
                ...state,
                vehicles : payload
            }    
        default:
        return state
    }
}
