import React, { useEffect } from "react";
import { useState } from "react";
import { useDispatch, useSelector } from "react-redux"
import { getAllStarships, resetState } from '../Redux/Actions/index'

export default function Pagination(){
    const dispatch = useDispatch()

    const dato = useSelector((state) => state.starships)

    let [next, setnext] = useState(dato.next)
    let [previous, setprevious] = useState(dato.previous)

    const previousPage = async () => {
        dispatch(resetState())
        await dispatch(getAllStarships(previous))
    }

    const nextPage = async () => {
        dispatch(resetState())
        await dispatch(getAllStarships(next))
    }

    useEffect(() => {
        setnext(dato.next)
        setprevious(dato.previous)
    },[dato])

    return(
        <div className="Pagination">
            <button onClick={previousPage}>Regresar</button>
            <button onClick={nextPage} >Siguiente</button>
        </div>
    )
}
