import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getAllPeople, getCharactersByFiml } from '../Redux/Actions/index.js'
import Card from './Card.js'

export default function Films() {
    const dispatch = useDispatch()

    const filmSW =  useSelector((state) => state.films)
    const characterSW = useSelector((state) => state.characters)

    const flag = false

    useEffect(() => {
        if(filmSW.results && characterSW.length < 79){
            for(let i=1; i < 9; i++){
                dispatch(getAllPeople(i)) 
            }
            flag = true
        }
    },[filmSW])

    // useEffect(() => {
    //     if(flag){
    //         dispatch(getCharactersByFiml())
    //         flag = false
    //     }
    // },[flag])

    return (
        <div className='Container'><h1>Films</h1>
            <div className='Films'>
                {filmSW.results ? filmSW.results.map(film => <Card key={film.episode_id} title={film.title} info={ characterSW ? film.people : null}/>): <h3>Cargando</h3>}
            </div>
        </div>
    )
}
