import React from "react";
import { useDispatch, useSelector } from "react-redux"
import { getAllStarships, resetState } from '../Redux/Actions/index'

export default function Pagination(){
    const dispatch = useDispatch()

    const dato = useSelector((state) => state.starships)

    const previousPage =  () => {
        if(dato.previous.substr(-1) !== null){
            dispatch(resetState())
            dispatch(getAllStarships(dato.previous.substr(-1)))
        }
    }

    const nextPage = () => {
        if(dato.next.substr(-1) !== null){
            dispatch(resetState())
            dispatch(getAllStarships(dato.next.substr(-1)))
        }
    }

    return(
        <div className="Pagination">
            <button onClick={previousPage}>Regresar</button>
            <button onClick={nextPage} >Siguiente</button>
        </div>
    )
}
