import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getAllPeople, getCharactersByFiml } from '../Redux/Actions/index.js'
import Card from './Card.js'

export default function Films() {
    const dispatch = useDispatch()

    const filmSW =  useSelector((state) => state.films)
    const characterSW = useSelector((state) => state.characters)

    useEffect(() => {
        if(filmSW.results){
            return dispatch(getAllPeople()) 
        }
    },[filmSW])

    useEffect(() => {
        if(characterSW.length > 70){
            dispatch(getCharactersByFiml())
        }
    },[characterSW])

    return (
        <div className='Container'><h1>Films</h1>
            <div className='Films'>
                {filmSW.results ? filmSW.results.map(film => <Card title={film.title} />): <h3>Cargando</h3>}
            </div>
        </div>
    )
}
