import React from 'react'
import ModalCharacter from './ModalCharacter'

export default function Card({title,info}){

    const handleShowModal = (info) => {
        const modal = document.querySelector('.ModalCharacterHide')
        // const modalh = document.querySelector('.ModalCharacter')
        if(modal){
            modal.remove('ModalCharacterHide')
            modal.classList.add('ModalCharacter')
        }
        // else if(modalh){
        //     modalh.remove('ModalCharater')
        //     modalh.classList.add('ModalCharaterHide')
        // }
        alert(modal)
    }

    return (
        <div>
            <div className='Card'>
                <h2>{title ? title : null}</h2>
                <div>
                    {info ? info.map(e => <p onClick={() => handleShowModal(e)}>{e.name}</p>):null}
                </div>
            </div>
        <ModalCharacter/>
        </div>
    )
}
