import React, { useEffect } from "react";
import { useState } from "react";
import { useDispatch, useSelector } from "react-redux"
import { getAllStarships, resetState } from '../Redux/Actions/index'

export default function Pagination(){
    const dispatch = useDispatch()

    const dato = useSelector((state) => state.starships)

    let [next, setnext] = useState(dato.next? dato.next.substr(-1): 1)
    let [previous, setprevious] = useState(dato.previous ? dato.previous.substr(-1) : 1)

    const previousPage =  () => {
        dispatch(resetState())
        setprevious(dato.previous.substr(-1))
        dispatch(getAllStarships(dato.previous.substr(-1)))
        console.log('previous ---'+dato.previous.substr(-1))
    }

    const nextPage = () => {
        dispatch(resetState())
        setnext(dato.next.substr(-1))
        dispatch(getAllStarships(dato.next.substr(-1)))
        console.log('next ---'+dato.next.substr(-1))
    }

    return(
        <div className="Pagination">
            <button onClick={previousPage}>Regresar</button>
            <button onClick={nextPage} >Siguiente</button>
        </div>
    )
}
