import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getAllPeople } from '../Redux/Actions/index.js'
import Card from './Card.js'

export default function Films() {
    const dispatch = useDispatch()

    const filmSW =  useSelector((state) => state.films)

    useEffect(() => {
        if(filmSW.results){
            filmSW.results.map(e => {
                return e.characters.map(c => {
                   return dispatch(getAllPeople(c))
                })  
            })
        }
    },[filmSW])

    return (
        <div className='Container'><h1>Films</h1>
            <div className='Films'>
                {filmSW.results ? filmSW.results.map(film => <Card title={film.title} />): <h3>Cargando</h3>}
            </div>
        </div>
    )
}
