import React from "react";
import { useState } from "react";
import { useDispatch, useSelector } from "react-redux"
import { getAllStarships } from '../Redux/Actions/index'

export default function Pagination(){
    const dispatch = useDispatch()

    const dato = useSelector((state) => state.starships)

    let [next, setnext] = useState(dato.next)
    let [previous, setprevious] = useState(dato.previous)

    const previousPage = () => {
        dispatch(getAllStarships(previous))
    }

    const nextPage = () => {
        dispatch(getAllStarships(next))
    }

    return(
        <div className="Pagination">
            <button onClick={previousPage}>Regresar</button>
            <button onClick={nextPage} >Siguiente</button>
        </div>
    )
}
